import TextField from "../../textField/textField.web";
import Button from "@material-ui/core/Button";
import CachedIcon from "@material-ui/icons/Cached";
import React from "react";

export const Amount = ({ amount, setAmountCallback, handleConversion }) =>
    <div className="amount-container" style={{ display: 'flex', flexDirection: 'column', margin: '0 auto', minWidth: 257 }}>
        <TextField {...{ initialAmount: amount, setAmount: e => setAmountCallback(e.target.value) }} />
        <Button
            onClick={handleConversion}
            variant="contained"
            color="secondary"
            startIcon={<CachedIcon />}
            style={{ backgroundImage: 'linear-gradient(to right, #0cebeb 0%, #20e3b2 51%, #0cebeb 100%)' }}
        >
            Convert
        </Button>
    </div>;
