import React, { Component } from 'react';
import { fetchLatestCurrencies } from '../../api/currency.api';
import CurrencyDropdown from '../currencyDropdown/currencyDropdown.web';
import TextField from '../textField/textField.web';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import CachedIcon from '@material-ui/icons/Cached';
import fx from 'money';
import './converter.css';

fx.base = 'EUR';

class Converter extends Component {
    state = {
        currencies: [],
        result: 'Currency Converter',
        fromCurrency: "EUR",
        toCurrency: "GBP",
        amount: 1,
    };

    componentDidMount = async () => {
        // A default while the api request returns values
        const currencyArray = ["EUR"];
        let rates = await fetchLatestCurrencies();
        fx.rates = rates;
        for (const key in rates) {
            currencyArray.push(key);
        }
        currencyArray.sort();
        this.setState({ currencies: currencyArray })
    };

    componentDidUpdate(prevProps, prevState) {
        if(prevState.toCurrency !== this.state.toCurrency) {
            this.handleConversion();
        }
    }


    handleConversion = () => {
        console.log('Handle Conversion called');
        if (this.state.fromCurrency !== this.state.toCurrency) {
            const convertedResult = fx(this.state.amount).from(this.state.fromCurrency).to(this.state.toCurrency);
            this.setState({ result: convertedResult });
        } else {
            this.setState({ result: "You cant convert the same currency!" })
        }
    };

    handleCurrencySelect = ({ target: { value, name }}) => {
        if (name === "From") {
            this.setState({ fromCurrency: value })
        }
        if (name === "To") {
            this.setState({ toCurrency: value });
        }
    };

    render() {
        const { fromCurrency, toCurrency, currencies, result, amount } = this.state;
        return (
            <div className="converter-container">
                {
                    <Typography className="result-text" variant="h4" component="h4">
                        {result > 0 ? result.toLocaleString(undefined, { style:"currency", currency: toCurrency }).split(" ")[0] : result}
                    </Typography>
                }
                        <div style={{ display: 'flex', flexDirection: 'row', textAlign: 'center', justifyContent: 'center', alignItems: 'baseline' }}>
                            <CurrencyDropdown {...{ currencies, currentCurrency: fromCurrency, selectCurrency: this.handleCurrencySelect, label: 'From' }}/>
                            <CurrencyDropdown {...{ currencies, currentCurrency: toCurrency, selectCurrency: this.handleCurrencySelect, label: 'To' }}/>
                        </div>
                        <div style={{ display: 'flex', flexDirection: 'column', margin: '0 auto', minWidth: 257 }}>
                            <TextField {...{ initialAmount: amount, setAmount: e => this.setState({ amount: e.target.value }) }} />
                            <Button
                                onClick={this.handleConversion}
                                variant="contained"
                                color="secondary"
                                startIcon={<CachedIcon />}
                            >
                                Convert
                            </Button>
                        </div>
            </div>
        )
    }
}

export default Converter;
