import React, { Component } from 'react';
import { fetchLatestCurrencies } from '../../api/currency.api';
import Container from '@material-ui/core/Container';
import fx from 'money';
import './converter.css';

class Converter extends Component {
    state = {
        currencies: [],
        result: null,
        fromCurrency: "EUR",
        toCurrency: "GBP",
        amount: 1,
    };

    componentDidMount = async () => {
        const currencyArray = ["EUR"];
        let rates = await fetchLatestCurrencies();
        fx.rates = rates;
        for (const key in rates) {
            currencyArray.push(key);
        }
        currencyArray.sort();
        this.setState({ currencies: currencyArray })
    };

    convertHandler = () => {
        if (this.state.fromCurrency !== this.state.toCurrency) {
            fx.base = this.state.fromCurrency;
            const convertedResult = fx(this.state.amount).from(this.state.fromCurrency).to(this.state.toCurrency);
            this.setState({ result: convertedResult });
        } else {
            this.setState({ result: "You cant convert the same currency!" })
        }
    };

    selectHandler = (event) => {
        if (event.target.name === "from") {
            this.setState({ fromCurrency: event.target.value })
        }
        if (event.target.name === "to") {
            this.setState({ toCurrency: event.target.value })
        }
    };

    render() {
        return (
            <Container>
                <h2><span>Currency </span> Converter <span role="img" aria-label="money">&#x1f4b5;</span> </h2>
                <div className="Form">
                    <div className="TopHalf">
                        <select
                            name="from"
                            onChange={(event) => this.selectHandler(event)}
                            value={this.state.fromCurrency}
                        >
                            {this.state.currencies.map(cur => (
                                <option key={cur}>{cur}</option>
                            ))}
                        </select>
                        <input
                            name="amount"
                            type="text"
                            value={this.state.amount}
                            onChange={event =>
                                this.setState({ amount: event.target.value })
                            }
                        />
                    </div>
                    <div className="BottomHalf">
                        <select
                            name="to"
                            onChange={(event) => this.selectHandler(event)}
                            value={this.state.toCurrency}
                        >
                            {this.state.currencies.map(cur => (
                                <option key={cur}>{cur}</option>
                            ))}
                        </select>
                        <button onClick={this.convertHandler}>Convert</button>
                    </div>
                    {this.state.result && <h3>{this.state.result}</h3>}
                    </div>
            </Container>
        )
    }
};

export default Converter;
