import React, { Component } from 'react';
import { fetchLatestCurrencies } from '../../api/currency.api';
import SimpleSelect from "../currencyDropdown/currencyDropdown.web";
import fx from 'money';
import './converter.css';

class Converter extends Component {
    state = {
        currencies: [],
        result: null,
        fromCurrency: "EUR",
        toCurrency: "GBP",
        amount: 1,
    };

    componentDidMount = async () => {
        const currencyArray = ["EUR"];
        let rates = await fetchLatestCurrencies();
        fx.rates = rates;
        for (const key in rates) {
            currencyArray.push(key);
        }
        currencyArray.sort();
        this.setState({ currencies: currencyArray })
    };

    // componentDidUpdate(prevProps, prevState){
    //     if(prevState.fromCurrency !== this.state.fromCurrency) {
    //         console.log('UPDATING BASE');
    //         fx.base = this.state.fromCurrency;
    //     }
    // }


    convertHandler = () => {
        fx.base = this.state.fromCurrency;
        if (this.state.fromCurrency !== this.state.toCurrency) {
            const convertedResult = fx(this.state.amount).from(this.state.fromCurrency).to(this.state.toCurrency);
            this.setState({ result: convertedResult });
        } else {
            this.setState({ result: "You cant convert the same currency!" })
        }
    };

    selectHandler = event => {
        if (event.target.name === "From") {
            fx.base = event.target.value;
            this.setState({ fromCurrency: event.target.value })
        }
        if (event.target.name === "To") {
            this.setState({ toCurrency: event.target.value })
        }
    };

    render() {
        const { fromCurrency, toCurrency, currencies, result, amount } = this.state;
        console.log('from currency ::', fromCurrency);
        console.log('to currency ::', toCurrency);
        console.log('fx base', fx.base);

        return (
            <div className="converter-container">
                    <div className="TopHalf">
                        <h3>Currency Converter</h3>
                        <SimpleSelect {...{ currencies, currentCurrency: fromCurrency, selectCurrency: this.selectHandler, label: 'From' }}/>
                        <input
                            name="amount"
                            type="text"
                            value={amount}
                            onChange={event =>
                                this.setState({ amount: event.target.value })
                            }
                        />
                    </div>
                    <div className="BottomHalf" >
                        <SimpleSelect {...{ currencies, currentCurrency: toCurrency, selectCurrency: this.selectHandler, label: 'To' }}/>
                        <button onClick={this.convertHandler}>Convert</button>
                    </div>
                    {result && <h3>{result}</h3>}
            </div>
        )
    }
};

export default Converter;
