import TextField from "../../textField/textField.web";
import Button from "@material-ui/core/Button";
import CachedIcon from "@material-ui/icons/Cached";
import React from "react";


export const Amount = ({ amount, setStateCallback }) =>
    <div className="amount-container" style={{ display: 'flex', flexDirection: 'column', margin: '0 auto', minWidth: 257 }}>
        <TextField {...{ initialAmount: amount, setAmount: e => setStateCallback({ amount: e.target.value }) }} />
        <Button
            onClick={this.handleConversion}
            variant="contained"
            color="secondary"
            startIcon={<CachedIcon />}
        >
            Convert
        </Button>
    </div>
