import React, { Fragment } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles(theme => ({
    formControl: {
        margin: theme.spacing(1),
        // minWidth: 120,
    },
    select: {
        width: 192,
        [theme.breakpoints.down(460)]: {
           width: '400px',
        },

    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
    cssLabel: {
        color: 'white',
    },
}));

export default function CurrencyDropdown({ currencies, currentCurrency, selectCurrency, label }) {
    const classes = useStyles();
    const inputLabel = React.useRef(null);
    const [labelWidth, setLabelWidth] = React.useState(0);

    React.useEffect(() => {
        setLabelWidth(inputLabel.current.offsetWidth);
    }, []);

    return (
        <Fragment>
            <FormControl variant="outlined" className={classes.formControl}>
                <InputLabel ref={inputLabel} id={`${label}-dropdown-label`}>
                    {label}
                </InputLabel>
                <Select
                    className={classes.select}
                    name={label}
                    labelId={`${label}-dropdown-label`}
                    id={`${label}-dropdown`}
                    value={currentCurrency}
                    onChange={selectCurrency}
                    labelWidth={labelWidth}
                >
                    {currencies.map(curr => <MenuItem key={curr} value={curr}>{curr}</MenuItem>)}
                </Select>
            </FormControl>
        </Fragment>
    );
}
