import Typography from "@material-ui/core/Typography";
import React from "react";

export const Result = ({ result, toCurrency}) =>
    <Typography className="result-text"  color="textSecondary" variant="h5" component="h5">
    {result > 0 ? result.toLocaleString(undefined, { style: "currency", currency: toCurrency }).split(' ')[0] : result}
</Typography>;
