import React from "react";
import CurrencyDropdown from "../../currencyDropdown/currencyDropdown.web";

export const Currencies = ({ currencies, fromCurrency, toCurrency, handleCurrencySelect }) =>
    <div className="currencies-container">
        <CurrencyDropdown {...{ currencies, currentCurrency: fromCurrency, selectCurrency: handleCurrencySelect, label: 'From' }}/>
        <CurrencyDropdown {...{ currencies, currentCurrency: toCurrency, selectCurrency: handleCurrencySelect, label: 'To' }}/>
    </div>;
