import TextField from "../../textField/textField.web";
import Button from "@material-ui/core/Button";
import CachedIcon from "@material-ui/icons/Cached";
import React from "react";

const styles = {
    alignItems: 'center',
    display: 'flex',
    flexDirection: 'column',
    margin: '0 auto',
    minWidth: 257,
    width: 400
};

export const Amount = ({ amount, setAmountCallback, handleConversion, buttonText }) =>
    <div className="amount-container" style={styles}>
        <TextField {...{ value: amount, label: 'Amount', onChangeCallback: e => setAmountCallback(e.target.value) }} />
        <Button
            aria-label="convert currency button"
            size="large"
            onClick={handleConversion}
            variant="contained"
            color="secondary"
            startIcon={<CachedIcon />}
        >
            {buttonText}
        </Button>
    </div>;
