import React from "react";
import CurrencyDropdown from "../../currencyDropdown/currencyDropdown.web";

export const Currencies = ({ currencies, fromCurrency, toCurrency, handleCurrencySelect }) =>
    <div className="currencies-container">
        <CurrencyDropdown {...{ values: currencies, currentValue: fromCurrency, onChangeCallback: handleCurrencySelect, label: 'From' }}/>
        <CurrencyDropdown {...{ values: currencies, currentValue: toCurrency, onChangeCallback: handleCurrencySelect, label: 'To' }}/>
    </div>;
